import React, { useState, useEffect } from 'react';
import './App.css';
import Pokemon from './Pokemon.js';
import masterball from './masterball.jpg'

function App() {

  const [data, setData] = useState({});
  const [pokemon, setPoke] = useState('ditto');
  const [link, setLink] = useState(`http://pokeapi.co/api/v2/pokemon/${pokemon}`);
  const [loading, setLoading] = useState(true)
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    const getStats = async () =>{
      setLoading(true);
      setIsError(false);
      try{
        const result = await fetch(link);
        const pokedata = await result.json();
        setData(pokedata)
      }
      catch (err){
        console.error(err);
        setIsError(true);
      }
      setLoading(false);
    }
    getStats();
  }, [link]);

  const display = {
    "true-true" : <p>How did you make this happen?</p>,
    "true-false" : <div>
                    <img className='pokeball' src='/masterball.jpg' alt='Wheres My PokeBall'></img>
                   </div>,
    "false-true" : <div>
                    <p className='errorpic'><img src='https://vignette.wikia.nocookie.net/mcleodgaming/images/d/d8/MissingNo..png/revision/latest/scale-to-width-down/340?cb=20131108185400' alt='MISSINGO HAS APPEARED'></img></p>
                    <h1 className='error'>You Have Encountered MissingNo!</h1>
                   </div>
    ,
    "false-false" : <Pokemon data={data}/>
  }

  return (
    <div className="App">
      <h3 className='head'>Enter Pokemon Name or ID</h3>
      <input className='entry' placeholder='Pokemon' onChange={e=>setPoke(e.target.value)}></input>
      <button className='funcs' onClick={e=>setLink(`http://pokeapi.co/api/v2/pokemon/${pokemon.toLowerCase()}`)}>Search</button>
      <div className='functions'>
          {display[`${loading}-${isError}`]}        
      </div>
    </div>
  );
}

export default App;
